import Link from "next/link";

const Layout = ({ title, children }) => (
  <div className="root">
    <nav className="navbar">
      <span>
        Welcome, <storong>Guest</storong>
      </span>
      <div>
        <Link href="/">
          <a>Home</a>
        </Link>
        <Link href="/profile">
          <a>Profile</a>
        </Link>
        <button>Logout</button>
        <Link href="/login">
          <a>Login</a>
        </Link>
      </div>
    </nav>
    <h1>{title}</h1>
    {children}

    <style jsx>
      {`
        .root {
          display: flex;
          align-items: center;
          justify-content: center;
          flex-directio: column;
        }
        .navbar {
          width: 100%;
          display: flex;
          justify-content: space-around;
        }
        a {
          margin-right: 0.5em;
        }
        button {
          text-decoration: underline;
          padding: 0;
          font: inherit;
          cursor: pointer;
          border-style: none;
          color: rgb(0, 0, 238);
        }
      `}
    </style>
  </div>
);

export default Layout;
